import React, { Component } from 'react';

import classes from './UserProfile.module.scss';

import NavigationItems from '../../components/UI/Navigation/NavigationItems/NavigationItems';
import Spinner from '../../components/UI/Spinner/Spinner';
import fire from '../../config/config';
import axios from '../../axios-orders';

class UserProfile extends Component {
	state = {
		uidInfo: '',
		loadng: true
	};

	componentDidMount() {
		fire.auth().onAuthStateChanged((user) => {
			if (user) {
				let userID = fire.auth().currentUser.uid;
				axios
					.get('https://webshop-9a548.firebaseio.com/users/' + userID + '.json')
					.then((response) => {
						this.setState({ uidInfo: response.data });
						console.log(this.state.uidInfo);
						this.setState({ loading: false });
					})
					.catch((e) => {
						console.log(e);
					});
				console.log(user.uid);
			} else {
				console.log('not logged in yet');
			}
		});
	}

	render() {
		let uidInfo;
		if (this.state.loading) {
			uidInfo = <Spinner />;
		} else {
			uidInfo = (
				<div className={classes.UserProfile}>
					<div className={classes.Heading}>
						<h1>User Profile</h1>
					</div>
					<div className={classes.Form}>
						<div className={classes.Img} />
						<div className={classes.Info}>
							<label>Name: </label>
							<p>Asd</p>
							<label>Country: </label>
							<p>Asd</p>
							<label>Zip Code: </label>
							<p>Asd</p>
							<label>City: </label>
							<p>Asd</p>
							<label>Street Address: </label>
							<p>Asd</p>
						</div>
					</div>
				</div>
			);
		}

		return (
			<React.Fragment>
				<NavigationItems />
				{uidInfo}
			</React.Fragment>
		);
	}
}

export default UserProfile;
