import React, { Component } from 'react';

import classes from './StoreItems.module.scss';
import StoreItem from './StoreItem/StoreItem';
import Spinner from '../../components/UI/Spinner/Spinner';

import axios from '../../axios-orders';

class StoreItems extends Component {
	state = {
		items: [],
		loading: true,
		firstItem: 0,
		lastItem: 6,
	};

	componentWillReceiveProps(prevProps, nextProps) { console.log('dwrp')
		if (JSON.stringify(prevProps) !== JSON.stringify(nextProps)) {
			this.setState({
				firstItem: this.props.firstItem,
				lastItem: this.props.lastItem
			})
		}
	}

	render() {
		let items = this.state.items.slice(this.state.firstItem, this.state.lastItem);
		let storeItem, shoes, socks, hoodies, shorts, tShirt, shirts, jackets, jeans, underwear;
		if (this.props.itemType) {
			shoes = this.props.itemType[0];
			socks = this.props.itemType[1];
			hoodies = this.props.itemType[2];
			shorts = this.props.itemType[3];
			tShirt = this.props.itemType[4];
			shirts = this.props.itemType[5];
			jackets = this.props.itemType[6];
			jeans = this.props.itemType[7];
			underwear = this.props.itemType[8];
		}
		if (this.state.loading) {
			storeItem = (
				<div className={classes.Spinner}>
					<Spinner />
				</div>
			);
		} else if (this.props.itemType) {
			let filteredItems = items.filter(
				(items) =>
					items.type === shoes ||
					items.type === socks ||
					items.type === hoodies ||
					items.type === shorts ||
					items.type === tShirt ||
					items.type === shirts ||
					items.type === jackets ||
					items.type === jeans ||
					items.type === underwear
			);
			storeItem = filteredItems.map((filteredItem, index) => (
				<StoreItem
					uniqueId={filteredItem.id}
					imgUrl={filteredItem.imgUrl}
					key={index}
					brand={filteredItem.brand}
					description={filteredItem.description}
					price={filteredItem.price}
					detailedDescription={filteredItem.detailedDescription}
				/>
			));
		} else {
			storeItem = items.map((filteredItem, index) => (
				<StoreItem
					uniqueId={filteredItem.id}
					imgUrl={filteredItem.imgUrl}
					key={index}
					brand={filteredItem.brand}
					description={filteredItem.description}
					price={filteredItem.price}
					detailedDescription={filteredItem.detailedDescription}
				/>
			));
		}

		return <div className={classes.StoreItems}>{storeItem}</div>;
	}
	componentDidMount() {
		axios.get('https://webshop-9a548.firebaseio.com/item.json').then((response) => {
			this.setState({ items: response.data });
			this.setState({ loading: false });
		});
	}

	componentWillReceiveProps() {
		this.setState({firstItem: this.props.firstItem})
		this.setState({lastItem: this.props.lastItem})
	}
}

export default StoreItems;
