import React, { Component } from 'react';

import classes from './Cart.module.scss';

import StoreItem from '../../components/UI/StoreItem/StoreItem';
import axios from '../../axios-orders';

class Cart extends Component {

	componentDidMount() {
		let userID = fire.auth().current
		axios.get('https://webshop-9a548.firebaseio.com/' ).then((response) => {
			this.setState({ items: response.data });
			this.setState({ loading: false });
		});
	}


	render() {
		let storeItem = this.props.cartItems.map((cartItem) => (
			<StoreItem
				imgUrl={cartItem.imgUrl}
				key={cartItem.imgUrl}
				brand={cartItem.brand}
				description={cartItem.description}
				price={cartItem.price}
			/>
		));

		return (
			<React.Fragment>
				<div className={classes.CartItems}>{storeItem}</div>
			</React.Fragment>
		);
	}
}

export default cart;
