import React, { Component } from 'react';
import axios from '../../axios-orders';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

import classes from './StoreItem.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMinusCircle } from '@fortawesome/free-solid-svg-icons';

class StoreItem extends Component {
	removeCartItem = () => {
		console.log(this.props.itemKey);
		axios
			.delete(
				'https://webshop-9a548.firebaseio.com/users/' +
					this.props.userId +
					'/cartItems/' +
					this.props.itemKey +
					'.json'
			)
			.then((u) => {
				window.location.reload();
			})
			.catch((e) => {
				console.log(e);
			});
	};

	render() {
		let button;
		if (window.location.href === 'http://localhost:3000/cart') {
			button = (
				<div onClick={this.removeCartItem} className={classes.RemoveCart}>
					<FontAwesomeIcon icon={faMinusCircle} />
				</div>
			);
		} else {
			button = (
				<div onClick={this.removeWishlistItem} className={classes.RemoveCart}>
					<FontAwesomeIcon icon={faMinusCircle} />
				</div>
			);
		}
		return (
			<div className={classes.StoreItem}>
				<div className={classes.Image}>
					<img src={this.props.imgUrl} alt={this.props.imgUrl} />
				</div>
				<div className={classes.Name}>
					<h3>{this.props.brand}</h3>
				</div>
				<div className={classes.Description}>
					<p>{this.props.description}</p>
				</div>
				<div className={classes.Price}>
					<p>{this.props.price}$</p>
				</div>
				{button}
			</div>
		);
	}
}

const mapStateToProps = (state) => {
	return {
		userId: state.userId
	};
};

export default connect(mapStateToProps)(StoreItem);
