import React, { Component } from 'react';

import CheckboxList from '../../components/CheckboxList/CheckboxList';
import StoreItems from '../StoreItems/StoreItems';

import classes from './Webshop.module.scss';

class Webshop extends Component {
	constructor(props){
		super(props)
		this.state = {
			type: null,
			firstItem: 0,
			lastItem: 6, 
		};
	}


	onChangeType = (newType) => {
		this.setState({ type: newType });
	};

	onPaginate = () => { console.log('bla')
		this.setState(prevState => ({
			firstItem: prevState.firstItem + 6,
			lastItem: this.state.lastItem + 6
		}))
	}

	render() {

		console.log(this.state.firstItem, this.state.lastItem)
		return (

			<React.Fragment>
				<div className={classes.Webshop}>
					<CheckboxList onChange={this.onChangeType.bind(this)} />
					<StoreItems firstItem={this.state.firstItem} lastItem={this.state.lastItem} itemType={this.state.type} />
				</div>
				<button onClick={(e) => this.onPaginate(e)}>Paginate</button>
			</React.Fragment>
		);
	}
}

export default Webshop;
