import React, { Component } from 'react';
import { connect } from 'react-redux';

import CheckboxList from '../../components/CheckboxList/CheckboxList';
import StoreItems from '../StoreItems/StoreItems';

import classes from './Webshop.module.scss';

class Webshop extends Component {
	state = {
		type: null,
		firstItem: this.props.firstItem,
		lastItem: this.props.lastItem
	};

	onChangeType = (newType) => {
		this.setState({ type: newType });
	};

	render() {
		console.log(
			this.state.firstItem, this.state.lastItem
		)
		return (
			<React.Fragment>
				<div className={classes.Webshop}>
					<CheckboxList onChange={this.onChangeType.bind(this)} />
					<StoreItems itemType={this.state.type} />
				</div>
				<button>Paginate</button>
			</React.Fragment>
		);
	}
}

const mapStateToProps = state => {
	return {
		firstItem: state.firstItem,
		lastItem: state.lastItem
	}
}

const mapDispatchToProps = dispatch => {
	return{
		onPaginate: (firstItem, lastItem) => dispatch({type: 'PAGINATE', firstItem: firstItem, lastItem: lastItem})
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(Webshop);
