import React, { Component } from 'react';

import classes from './Register.module.scss';

import Input from '../../../components/UI/Input/Input';
import Layout from '../../../components/Layout/Layout';
import Button from '../../../components/UI/Button/GeneralButton/Button';
import fire from '../../../config/config';

class Register extends Component {
	constructor(props) {
		super(props);
		this.signUp = this.signUp.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.state = {
			email: '',
			password: '',
			verifyPassword: '',
			error: '',
			fullName: '',
			country: '',
			city: '',
			zipCode: ''
		};
	}

	signUp(e) {
		e.preventDefault();
		let password = this.state.password;
		let verifyPassword = this.state.verifyPassword;
		if (password === verifyPassword) {
			fire
				.auth()
				.createUserWithEmailAndPassword(this.state.email, this.state.verifyPassword)
				.then((u) => {
					this.props.history.push('/');
					alert('You have successfully created account and are now signed in!');
				})
				.catch((error) => {
					let errorMessage = error.message;
					if (this.state.email === '' || this.state.verifyPassword === '') {
						errorMessage = 'Please fill up all the fields.';
					}
					this.setState({ error: errorMessage });
				});
		} else {
			this.setState({ error: 'Passwords do not match!' });
		}
	}

	handleChange(e) {
		this.setState({ [e.target.name]: e.target.value });
	}

	render() {
		let errorStyle = null;
		if (this.state.error) {
			errorStyle = {
				color: 'red',
				marginTop: '-22px',
				marginBottom: '10px',
				marginLeft: '100px',
				fontSize: '.7em'
			};
		}

		return (
			<React.Fragment>
				<Layout />
				<form className={classes.Register}>
					<h3>Register your account here.</h3>
					<div className={classes.Wrapper}>
						<div className={classes.Left}>
							<label className={classes.Label}>E-mail address</label>
							<Input
								value={this.state.email}
								onChange={this.handleChange}
								type="email"
								name="email"
								placeholder="Enter e-mail"
							/>
							<label className={classes.Label}>Password</label>
							<Input
								value={this.state.password}
								onChange={this.handleChange}
								type="password"
								name="password"
								placeholder="Enter password"
								autoComplete="on"
							/>
							<label className={classes.Label}>Verify Password</label>
							<Input
								value={this.state.verifyPassword}
								onChange={this.handleChange}
								type="password"
								name="verifyPassword"
								placeholder="Verify Password"
								autoComplete="on"
							/>
						</div>
						<div className={classes.Right}>
							<label className={classes.Label}>Full name</label>
							<Input
								value={this.state.fullName}
								onChange={this.handleChange}
								type="text"
								name="fullName"
								placeholder="Enter your full name"
							/>
							<label className={classes.Label}>Country name</label>
							<Input
								value={this.state.country}
								onChange={this.handleChange}
								type="text"
								name="country"
								placeholder="Enter your country"
							/>
							<label className={classes.Label}>City name</label>
							<Input
								value={this.state.city}
								onChange={this.handleChange}
								type="text"
								name="city"
								placeholder="Enter your city"
							/>
						</div>
					</div>
					<p style={errorStyle}>{this.state.error}</p>
					<div className={classes.Button}>
						<Button onClick={this.signUp}>Register</Button>
					</div>
					<a href="/login">Already have an account?</a>
				</form>
			</React.Fragment>
		);
	}
}

export default Register;
