import React, { Component } from 'react';

import classes from './Login.module.scss';

import Layout from '../../components/Layout/Layout';
import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/GeneralButton/Button';

class Login extends Component {
	render() {
		return (
			<React.Fragment>
				<Layout />
				<form className={classes.Login}>
					<label>E-mail address</label>
					<Input value={this.state.email} onChange={this.handleChange} type="e-mail" name="mail" placeholder="Enter e-mail" />
					<label>Password</label>
					<Input value={this.state.pasword} onChange={this.handleChange} type="password" name="password" placeholder="Enter password" autoComplete="on" />
					<div className={classes.Buttons}>
						<Button>Log In</Button>
						<Button>Register</Button>
					</div>
				</form>
			</React.Fragment>
		);
	}
}

export default Login;
