import React, { Component } from 'react';

import classes from './Cart.module.scss';

import StoreItem from '../../components/UI/StoreItem/StoreItem';
import axios from '../../axios-orders';
import fire from '../../config/config';
import Spinner from '../../components/UI/Spinner/Spinner';

class Cart extends Component {
	state = {
		cartItems: '',
		isLoggedIn: false,
		loading: true
	};

	componentDidMount() {
		fire.auth().onAuthStateChanged((user) => {
			this.setState({ isLoggedIn: true });
			if (user) {
				axios
					.get('https://webshop-9a548.firebaseio.com/users/' + user.uid + '/cartItems.json')
					.then((response) => {
						this.setState({ cartItems: response.data });
						this.setState({ loading: false });
					})
					.catch((e) => {
						console.log(e);
					});
			}
		});
	}

	render() {
		let storeItem = <Spinner />
		if (this.state.isLoggedIn) {
			if(this.state.cartItems){
				const cartItems = Object.values(this.state.cartItems);

				storeItem = cartItems.map((cartItem) => (
					<StoreItem
						imgUrl={cartItem.imgUrl}
						key={cartItem.imgUrl}
						brand={cartItem.brand}
						description={cartItem.description}
						price={cartItem.price}
					/>
				));
			}
		}else{
			storeItem = <p>Please login to view your cart</p>
		}

		return (
			<React.Fragment>
				<div className={classes.CartItems}>{storeItem}</div>
			</React.Fragment>
		);
	}
}

export default Cart;
